import React, { useState, useEffect, useRef } from 'react';
import { Search, X } from 'lucide-react';
import { searchApi } from '../services/api';

const SearchBar = ({ onSearch, onInstantAnswer }) => {
  const [query, setQuery] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const suggestionTimeoutRef = useRef(null);

  useEffect(() => {
    if (query.trim().length > 2) {
      // Clear previous timeout
      if (suggestionTimeoutRef.current) {
        clearTimeout(suggestionTimeoutRef.current);
      }

      // Set new timeout for debouncing
      suggestionTimeoutRef.current = setTimeout(async () => {
        try {
          const suggestion = await searchApi.autoComplete(query);
          if (suggestion) {
            // Handle both string and object responses
            const suggestionText = typeof suggestion === 'string' ? suggestion : suggestion.phrase;
            if (suggestionText) {
              setSuggestions([suggestionText]);
              setShowSuggestions(true);
            }
          }
        } catch (error) {
          console.error('Failed to get suggestions:', error);
        }
      }, 300);
    } else {
      setSuggestions([]);
      setShowSuggestions(false);
    }

    return () => {
      if (suggestionTimeoutRef.current) {
        clearTimeout(suggestionTimeoutRef.current);
      }
    };
  }, [query]);

  const handleSearch = async () => {
    if (!query.trim()) return;

    setIsLoading(true);
    setShowSuggestions(false);

    try {
      // Try to get instant answer first
      const instantAnswer = await searchApi.getInstantAnswer(query);
      if (instantAnswer && onInstantAnswer) {
        onInstantAnswer(instantAnswer);
      }

      // Perform regular search
      if (onSearch) {
        onSearch(query);
      }
    } catch (error) {
      console.error('Search failed:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSuggestionClick = (suggestion) => {
    setQuery(suggestion);
    setShowSuggestions(false);
    // Trigger search with the suggestion
    setTimeout(() => {
      if (onSearch) onSearch(suggestion);
    }, 100);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  const clearQuery = () => {
    setQuery('');
    setSuggestions([]);
    setShowSuggestions(false);
  };

  return (
    <div className="relative w-full max-w-2xl mx-auto">
      <div className="relative">
        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
          <Search className="h-5 w-5 text-gray-400" />
        </div>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Search anything..."
          className="block w-full pl-10 pr-12 py-3 border border-gray-300 rounded-lg leading-5 bg-white placeholder-gray-500 focus:outline-none focus:placeholder-gray-400 focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
        />
        {query && (
          <button
            onClick={clearQuery}
            className="absolute inset-y-0 right-12 pr-3 flex items-center"
          >
            <X className="h-5 w-5 text-gray-400 hover:text-gray-600" />
          </button>
        )}
        <button
          onClick={handleSearch}
          disabled={isLoading || !query.trim()}
          className="absolute inset-y-0 right-0 px-4 flex items-center bg-primary-500 text-white rounded-r-lg hover:bg-primary-600 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {isLoading ? (
            <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
          ) : (
            <Search className="h-5 w-5" />
          )}
        </button>
      </div>

      {/* Suggestions dropdown */}
      {showSuggestions && suggestions.length > 0 && (
        <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-lg shadow-lg">
          {suggestions.map((suggestion, index) => (
            <button
              key={index}
              onClick={() => handleSuggestionClick(typeof suggestion === 'string' ? suggestion : suggestion.phrase)}
              className="w-full px-4 py-2 text-left hover:bg-gray-100 focus:bg-gray-100 focus:outline-none"
            >
              {typeof suggestion === 'string' ? suggestion : suggestion.phrase}
            </button>
          ))}
        </div>
      )}
    </div>
  );
};

export default SearchBar;
